&lt;h4 id=&#34;垂直顶部对齐&#34;&gt;&lt;a href=&#34;#垂直顶部对齐&#34; class=&#34;headerlink&#34; title=&#34;垂直顶部对齐&#34;&gt;&lt;/a&gt;垂直顶部对齐&lt;/h4&gt;&lt;pre&gt;&lt;code&gt;layout = QVBoxLayout()
