&lt;/tr&gt;
&lt;/thead&gt;
&lt;tbody&gt;&lt;tr&gt;
&lt;td&gt;assertEqual(a,b)&lt;/td&gt;
&lt;td&gt;a==b&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;assertNotEqual(a,b)&lt;/td&gt;
&lt;td&gt;a!=b&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;assertTrue(x)&lt;/td&gt;
&lt;td&gt;x为True&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;asserFalse(x)&lt;/td&gt;
&lt;td&gt;x为False&lt;/td&gt;
&lt;/tr&gt;
&lt;tr&gt;
&lt;td&gt;assertIn(item,list)&lt;/td&gt;
&lt;td&gt;item在list中&lt;/td&gt;
&lt;/tr&gt;
