&lt;p&gt;&lt;strong&gt;é€‰æ‹©å›¾ç‰‡ä¸æ˜¾ç¤ºå›¾ç‰‡å’Œå›¾ç‰‡è·¯å¾„&lt;/strong&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;import tkinter
import tkinter.filedialog
import tkinter.ttk
from tkinter import *
from PIL import Image
from PIL import ImageTk
class App():
    # æ„é€ å‡½æ•°
    def __init__(self, master=None, value=None, name=None):
        self.select()
    # é€‰æ‹©æ–‡ä»¶
    def select(self):
        btn = Button(root, text=&amp;#39;ç‚¹å‡»é€‰æ‹©æ–‡ä»¶&amp;#39;, command=self.openfile, font=12, fg=&amp;quot;white&amp;quot;, bg=&amp;quot;green&amp;quot;)
        btn.pack(ipadx=&amp;#39;10&amp;#39;, ipady=&amp;#39;1&amp;#39;)
    #  æ‰“å¼€æ–‡ä»¶å¹¶æ˜¾ç¤ºè·¯å¾„
    def openfile(self):
        global filename
        filename = tkinter.filedialog.askopenfilename()
        if filename != &amp;#39;&amp;#39;:
            lb.config(text=&amp;#39;æ‚¨é€‰æ‹©çš„æ–‡ä»¶æ˜¯ : &amp;#39; + filename)
            lb.pack()
            self.showpic()
        else:
            lb.config(text=&amp;#39;æ‚¨æ²¡æœ‰é€‰æ‹©ä»»ä½•æ–‡ä»¶&amp;#39;)
            lb.pack()
    # æ˜¾ç¤ºå›¾ç‰‡
    def showpic(self):
        Img = Image.open(filename)
        Img = Img.resize((int(500), int(300)), Image.ANTIALIAS)
        img_png = ImageTk.PhotoImage(Img)
        label_Img = tkinter.Label(root, image=img_png)
        label_Img.pack(anchor=&amp;#39;center&amp;#39;)
        root.mainloop()

root = tkinter.Tk()
root.title(&amp;#39;å›¾åƒè¯†åˆ«&amp;#39;)
root.geometry(&amp;#39;600x500&amp;#39;)
